mapper.select:
select c.id, c.userId, c.description, c.location, c.registerDate, c.authCode 
from usercctv c, user u  
where c.userId = u.id and u.email=#{email}

select c.id, c.userId, c.description, c.location, c.registerDate, c.authCode 
from usercctv c, user u  
where c.userId = u.id and c.id=#{id}
